import React, { Component } from "react";
import "./App.css";
import fire from "./config/Fire";
import Change from "./Change";
import Home from "./pages/Home";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {}
    };
  }

  componentDidMount() {
    this.authListener();
  }

  authListener() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    });
  }

  render() {
    return (
      <div className="">
        <div className="App_body">{this.state.user ? <Home /> : <Change />}</div>
      </div>
    )
  }
}

export default App;
